<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Question_category_model extends CI_Model{    //input values    public function input_values()    {		$data = array(            'lang_id' => $this->input->post('lang_id', true),            'question_category_name' => $this->input->post('question_category_name', true),            'short_desc' => $this->input->post('short_desc', true),            'large_desc' => $this->input->post('large_desc', true),            'status' => $this->input->post('status', true),        );        return $data;    }    //add    public function add()    {        $data = $this->input_values();        return $this->db->insert('question_category', $data);    }    //update    public function update($id)    {        //set values        $data = $this->input_values();        $this->db->where('id', $id);        return $this->db->update('question_category', $data);    }	    //get all data    public function get_all_categories()    {        $this->db->order_by('question_category.question_category_name', 'ASC');        $query = $this->db->get('question_category');        return $query->result();    }    //get data by id    public function get_category($id)    {        $this->db->where('question_category.id', $id);        $query = $this->db->get('question_category');        return $query->row();    }    //get unique    public function get_category_by_name($question_category_name)    {        $this->db->where('question_category.question_category_name', $question_category_name);        $query = $this->db->get('question_category');        return $query->row();    }        //delete    public function delete($id)    {        $category = $this->get_category($id);        if (!empty($category)) {			$this->db->where('question_category.id', $id);            return $this->db->delete('question_category');        } else {            return false;        }    }	    //check if 