<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Topic_model extends CI_Model{    //input values    public function input_values()    {		$data = array(            'lang_id' => $this->input->post('lang_id', true),            'topic' => $this->input->post('topic', true),            'short_desc' => $this->input->post('short_desc', true),            'large_desc' => $this->input->post('large_desc', true),            'status' => $this->input->post('status', true),        );        return $data;    }    //add    public function add()    {        $data = $this->input_values();        return $this->db->insert('topics', $data);    }    //update    public function update($id)    {        //set values        $data = $this->input_values();        $this->db->where('id', $id);        return $this->db->update('topics', $data);    }	    //get all data    public function get_all_topics()    {        $this->db->order_by('topics.topic', 'ASC');        $query = $this->db->get('topics');        return $query->result();    }    //get data by id    public function get_topic($id)    {        $this->db->where('topics.id', $id);        $query = $this->db->get('topics');        return $query->row();    }    //get unique    public function get_topic_by_name($topic)    {        $this->db->where('topics.topic', $topic);        $query = $this->db->get('topics');        return $query->row();    }        //delete    public function delete($id)    {        $topic = $this->get_topic($id);        if (!empty($topic)) {			$this->db->where('topics.id', $id);            return $this->db->delete('topics');        } else {            return false;        }    }	    //check if field is unique    public function is_unique_field($topic, $topic_id = 0)    {        $topic = $this->topic_model->get_topic_by_name($topic);         //if id doesnt exists        if ($topic_id == 0)         {            if (empty($topic))             {                return true;            }             else             {                return false;            }        }        if ($topic_id != 0)         {            if (!empty($topic) && $topic->id == $topic_id)             {                //field taken                return true;            }             else             {                return false;            }        }    }}